import { useLocation } from "react-router-dom";
import "./product.css";
import Chart from "../../components/chart/Chart";
import { Publish } from "@material-ui/icons";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useMemo, useState } from "react";
import { userRequest } from "../../requestMethods";
import { updateProduct } from "../../redux/apiCalls";
import { useHistory } from "react-router-dom";

export default function Product() {
  const location = useLocation();
  const productId = location.pathname.split("/")[2];
  const [product, setProduct] = useState({});
  const [productName, setProductName] = useState("");
  const [productDesc, setProductDesc] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productInStock, setProductInStock] = useState(false);
  const [productImg, setProductImg] = useState(null);
  const dispatch = useDispatch();
  const history = useHistory();

  useEffect(() => {
    const getProduct = async () => {
      try {
        const res = await userRequest.get(`/products/${productId}`);
        setProduct(res.data);
        setProductName(res.data.title);
        setProductDesc(res.data.desc);
        setProductPrice(res.data.price);
        setProductInStock(res.data.inStock);
        setProductImg(res.data.img);
      } catch (error) {
        console.log(error);
      }
    };
    getProduct();
  }, [productId]);

  const handleUpdate = async () => {
    try {
      const updatedProduct = {
        ...product,
        title: productName,
        desc: productDesc,
        price: productPrice,
        inStock: productInStock
      };

      const formData = new FormData();
      formData.append("title", productName);
      formData.append("desc", productDesc);
      formData.append("price", productPrice);
      formData.append("inStock", productInStock);
      formData.append("img", productImg);

      await updateProduct(product._id, formData, dispatch);
      history.push("/products");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="product">
      {/* Product details and form */}
      <form className="productForm">
        <div className="productFormLeft">
          <label>Product Name</label>
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
          />
          <label>Product Description</label>
          <input
            type="text"
            value={productDesc}
            onChange={(e) => setProductDesc(e.target.value)}
          />
          <label>Price</label>
          <input
            type="text"
            value={productPrice}
            onChange={(e) => setProductPrice(e.target.value)}
          />
          <label>In Stock</label>
          <select
            name="inStock"
            id="idStock"
            value={productInStock}
            onChange={(e) => setProductInStock(e.target.value)}
          >
            <option value={true}>Yes</option>
            <option value={false}>No</option>
          </select>
        </div>
        <div className="productFormRight">
          <div className="productUpload">
            <img src={productImg} alt="" className="productUploadImg" />
            <label htmlFor="file">
              <Publish />
            </label>
            <input
              type="file"
              id="file"
              style={{ display: "none" }}
              onChange={(e) => setProductImg(e.target.files[0])}
            />
          </div>
          <button className="productButton" onClick={handleUpdate}>
            Update
          </button>
        </div>
      </form>
    </div>
  );
}
