import {
  CalendarToday,
  LocationSearching,
  MailOutline,
  PermIdentity,
  PhoneAndroid,
  Publish,
} from "@material-ui/icons";
// import { Link } from "react-router-dom";
import "./user.css";
import { NavLink } from "react-router-dom/cjs/react-router-dom.min";
import { publicRequest } from "../../requestMethods";
import { useEffect, useState } from "react";

export default function User() {
  const [editingUser, setEditingUser] = useState(null);

  const [data, setData] = useState([]);

  // 
 useEffect(()=>{
  fetchData();
 },[]);

 const fetchData = async (id) => {
  try {
    const res = await publicRequest.get(`/users/${id}}`);
    const formattedData = res.data.map((item) => ({
      ...item,
      id: item._id // Rename _id to id
    }));
    setData(formattedData);
    console.log(formattedData)
  } catch (error) {
    console.log("Error during fetching data", error);
  }
};

  const handleEditButton = async (req) => {
    try {
      await publicRequest.put(`/user/${req.params.id}`, editingUser);
    } catch (error) {
      console.error("error on editing user", error);
    }
  };

  return (
    <div className="user">
      <div className="userTitleContainer">
        <h1 className="userTitle">Edit User</h1>
        <NavLink to="/newUser">
          <button className="userAddButton">Create</button>
        </NavLink>
      </div>
      <div className="userContainer">
        <div className="userShow">
          <div className="userShowTop">
            <img
              src="https://images.pexels.com/photos/1152994/pexels-photo-1152994.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500"
              alt=""
              className="userShowImg"
            />
            <div className="userShowTopTitle">
              <span className="userShowUsername">Anna Becker</span>
              <span className="userShowUserTitle">Software Engineer</span>
            </div>
          </div>
          <div className="userShowBottom">
            <span className="userShowTitle">Account Details</span>
            <div className="userShowInfo">
              <PermIdentity className="userShowIcon" />
              <span className="userShowInfoTitle">annabeck99</span>
            </div>
            <div className="userShowInfo">
              <CalendarToday className="userShowIcon" />
              <span className="userShowInfoTitle">10.12.1999</span>
            </div>
            <span className="userShowTitle">Contact Details</span>
            <div className="userShowInfo">
              <PhoneAndroid className="userShowIcon" />
              <span className="userShowInfoTitle">+1 123 456 67</span>
            </div>
            <div className="userShowInfo">
              <MailOutline className="userShowIcon" />
              <span className="userShowInfoTitle">annabeck99@gmail.com</span>
            </div>
            <div className="userShowInfo">
              <LocationSearching className="userShowIcon" />
              <span className="userShowInfoTitle">New York | USA</span>
            </div>
          </div>
        </div>
        <div className="userUpdate">
          <span className="userUpdateTitle">Edit</span>
          <form className="userUpdateForm">
            <div className="userUpdateLeft">
              <div className="userUpdateItem">
                <label>Username</label>
                <input
                  type="text"
                  value={editingUser ? editingUser.username : ''}
                  onChange={(e) => setEditingUser({ ...editingUser, username: e.target.value })}
                  className="userUpdateInput"
                />
              </div>
              <div className="userUpdateItem">
                <label>Email</label>
                <input
                  type="text"
                  value={editingUser ? editingUser.email : ''}
                  onChange={(e) => setEditingUser({ ...editingUser, email: e.target.value })}
                  className="userUpdateInput"
                />
              </div>
              <div className="userUpdateItem">
                <label>First name</label>
                <input
                  type="text"
                  value={editingUser ? editingUser.firstName : ''}
                  onChange={(e) => setEditingUser({ ...editingUser, firstName: e.target.value })}
                  className="userUpdateInput"
                />
              </div>
              <div className="userUpdateItem">
                <label>Last name</label>
                <input
                  type="text"
                  value={editingUser ? editingUser.lastName : ''}
                  onChange={(e) => setEditingUser({ ...editingUser, lastName: e.target.value })}
                  className="userUpdateInput"
                />
              </div>
              {/* <div className="userUpdateItem">
                <label>Address</label>
                <input
                  type="text"
                  placeholder="New York | USA"
                  className="userUpdateInput"
                />
              </div> */}
            </div>
            <div className="userUpdateRight">
              <div className="userUpdateUpload">
                <img
                  className="userUpdateImg"
                  src={editingUser ? editingUser.img : ''}
                />
                <label htmlFor="file">
                  <Publish className="userUpdateIcon" />
                </label>
                <input type="file" id="file" style={{ display: "none" }} />
              </div>
              <button className="userUpdateButton" onClick={handleEditButton}>Update</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
