import { DataGrid } from "@material-ui/data-grid";
import { DeleteOutline } from "@material-ui/icons";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { deleteOrder, getOrders } from "../../redux/apiCalls";
import { NavLink } from "react-router-dom/cjs/react-router-dom";

export default function OrderList() {
  const dispatch = useDispatch();
  const orders = useSelector((state) => state.order.orders);

  useEffect(() => {
    getOrders(dispatch);
  }, [dispatch]);

  const handleDelete = (id) => {
    deleteOrder(id, dispatch);
  };

  const columns = [
    { field: "_id", headerName: "ID", width: 220 },
    {
      field: "products",
      headerName: "Products",
      width: 400,
      renderCell: (params) => {
        return (
          <div className="productListItem" 
          style={{
            overflow:'scroll',
            maxHeight:'200px',
            padding:'10px',
            }}>
            {params.row.products.map((item, index) => (
              <div key={index} style={{ marginBottom: '10px',maxHeight:'150px', overflow:'scroll' }}>
                <span>Quantity: {item.quantity}</span> {/* Display quantity */}
                {/* <div> */}
                  <p>{item.product.title}</p> 
                  <p>Description: {item.product.desc}</p> Display product description
                  <p>Price: ${item.product.price}</p> Display product price
                {/* </div> */}
              </div>
            ))}
          </div>
        );
      },
    },
    
   //  { field: "quantity", headerName: "Quantity", width: 200 },
    {
      field: "total",
      headerName: "Amount",
      width: 160,
    },
    {
      field: "action",
      headerName: "Action",
      width: 150,
      renderCell: (params) => {
        return (
          <>
            <NavLink to={"/orders/" + params.row._id}>
              <button className="productListEdit">Edit</button>
            </NavLink>
            <DeleteOutline
              className="productListDelete"
              onClick={() => handleDelete(params.row._id)}
            />
          </>
        );
      },
    },
  ];

  return (
    <div className="productList">
      <DataGrid
        rows={orders}
        disableSelectionOnClick
        columns={columns}
        getRowId={(row) => row._id}
        pageSize={8}
        checkboxSelection
      />
    </div>
  );
}
